<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Site extends MX_Controller {		public function __construct(){		parent::__construct();	}			public function index (){			$config = & get_config();			if (isset($config['login_cas']) && $config['login_cas'])				$this->index_cas();			else 				$this->index_tmp();	}	public function index_tmp(){					/*$pengguna->username="51982";		$pengguna->nama="user 51982";		$pengguna->id_pengguna_grup="1";		$pengguna->id="12";		$pengguna->foto="";		$this->session->set_userdata('pengguna', $pengguna);		*/		//redirect(site_url('/dashboard'));		//redirect(site_url('/stomp/'));		redirect(site_url('home'));	}		public function index_cas(){		$myid = $this->cas->getmycas_login(array('idm_init', 'idm_appr'));		redirect('home');	}		public function login(){		$username = $this->input->post('username');		$pass 	  = $this->input->post('password');		$password = MD5($pass);				$src = $this->db->get_where('pengguna', array (			'username' 	=> $username,			'password'	=> $password,		));				if ($src->num_rows() == 0) {			$this->session->set_flashdata('status', 'failed');			redirect($this->cas->cas_server_url . "login");		}		else {			$pengguna = $src->row();			$this->session->set_userdata('pengguna', $pengguna);						redirect(site_url('/dashboard'));		}	}		public function logout(){		//$this->session->sess_destroy();		//$this->load->library('cas');		//$this->cas->logout();		//redirect("https://cas.bni.co.id");		//redirect("/");		session_destroy();		redirect($this->cas->cas_server_url . "login");	}	}/* End of file site.php *//* Location: ./application/controllers/site.php */